static void user_atk_window_on_destroy(void *context)
{
    user_atk_window_t *win = (user_atk_window_t *)context;
    if (!win)
    {
        return;
    }

    win->window = NULL;
    win->image = NULL;
    win->pixels = NULL;
    win->closed = true;
    if (g_focus_window == win)
    {
        g_focus_window = NULL;
