}

static void user_atk_insert(user_atk_window_t *win)
{
    win->prev = NULL;
    win->next = g_windows_head;
    if (g_windows_head)
    {
        g_windows_head->prev = win;
    }
    g_windows_head = win;
}

static void user_atk_remove(user_atk_window_t *win, bool closing_kernel)
{
