        g_windows_head = win->next;
    }
    if (win->next)
    {
        win->next->prev = win->prev;
    }
    win->next = NULL;
    win->prev = NULL;

    if (!closing_kernel && win->window)
    {
        win->destroying = true;
        atk_window_close(atk_state_get(), win->window);
        win->destroying = false;
    }

