static void user_atk_queue_event(user_atk_window_t *win, const user_atk_event_t *event)
{
    if (!win || !event)
    {
        return;
    }
#if USER_ATK_DEBUG
    user_atk_log_pair("enqueue event", win->handle, event->type);
    if (event->type == USER_ATK_EVENT_MOUSE)
