void atk_user_close(atk_user_window_t *win)
{
    if (!win)
    {
        return;
    }
    if (win->handle)
    {
        sys_ui_close(win->handle);
        win->handle = 0;
    }
    if (win->buffer)
    {
        video_surface_detach();
        free(win->buffer);
        win->buffer = NULL;
    }
    win->buffer_bytes = 0;
    win->width = 0;
    win->height = 0;
    win->track_dirty = false;
}

