void atk_state_theme_commit(atk_state_t *state)
{
    if (!state)
    {
        return;
    }
    state->theme_crc = atk_theme_checksum(&state->theme);
    atk_guard_reset(&state->theme_guard_front, &state->theme_guard_back);
}

