};

// Rotate bits right
__uint32_t rotr(__uint32_t x, __uint16_t a)
{
	return (x >> a) | (x << (32 - a));
};

// Shift bits right
__uint32_t shr(__uint32_t x, __uint16_t b)
{
	return (x >> b);
};

__uint32_t SIG0(__uint32_t x)
{
	return (rotr(x, 2) ^ rotr(x, 13) ^ rotr(x, 22));
};

__uint32_t SIG1(__uint32_t x)
