};

// Choose
__uint32_t Ch(__uint32_t x,__uint32_t y,__uint32_t z)
{
	return ((x & y) ^ (~(x)&z));
};

// Majority decision
__uint32_t Maj(__uint32_t x,__uint32_t y,__uint32_t z)
{
	return ((x & y) ^ (x & z) ^ (y & z));
};

