static void bignum_reduce_wide(const bignum_wide_t *value, const bignum_t *mod, bignum_t *out)
{
    if (mod->length == 0)
    {
        bignum_init(out);
        return;
    }

    bignum_wide_t rem = *value;
    bignum_wide_normalize(&rem);

    while (rem.length > mod->length ||
           (rem.length == mod->length && bignum_wide_compare_shifted(&rem, mod, 0) >= 0))
    {
        size_t shift = rem.length - mod->length;
        if (shift > 0 && bignum_wide_compare_shifted(&rem, mod, shift) < 0)
        {
            shift--;
        }

        uint64_t rem_high = rem.words[rem.length - 1];
        uint64_t rem_next = (rem.length > 1) ? rem.words[rem.length - 2] : 0;
        uint64_t mod_high = mod->words[mod->length - 1];
        if (mod_high == 0)
        {
            mod_high = 1;
        }
        uint64_t numerator = (rem_high << 32) | rem_next;
        uint64_t q = numerator / mod_high;
        if (q == 0)
        {
            q = 1;
        }
        if (q > 0xFFFFFFFFULL)
        {
            q = 0xFFFFFFFFULL;
        }

        bool reduced = false;
        while (!reduced)
        {
            bignum_wide_t trial = rem;
            if (bignum_wide_sub_mul_word(&trial, mod, shift, (uint32_t)q))
            {
