                    }
                    else
                    {
                        bignum_wide_normalize(&rem);
                    }
                    break;
                }
                q--;
            }
        }
        bignum_wide_normalize(&rem);
    }

    size_t copy_len = (rem.length > BIGNUM_MAX_WORDS) ? BIGNUM_MAX_WORDS : rem.length;
    if (copy_len > 0)
    {
        memcpy(out->words, rem.words, copy_len * sizeof(uint32_t));
    }
    if (copy_len < BIGNUM_MAX_WORDS)
    {
        memset(out->words + copy_len, 0, (BIGNUM_MAX_WORDS - copy_len) * sizeof(uint32_t));
    }
