static size_t bignum_bit_length(const bignum_t *num)
{
    if (num->length == 0)
    {
        return 0;
    }
    uint32_t last = num->words[num->length - 1];
    size_t bits = (num->length - 1) * 32;
    while (last != 0)
    {
        bits++;
        last >>= 1U;
    }
    return bits;
}
