static vfs_node_t *ensure_directory_path(const char *path)
{
    if (!path || path[0] == '\0')
    {
        return NULL;
    }
    if (path[0] != '/')
    {
        return NULL;
    }
    if (path[1] == '\0')
    {
        return vfs_root();
    }

    size_t path_len = strlen(path);
    if (path_len >= 256)
    {
        return NULL;
    }

    char partial[256];
    size_t partial_len = 1;
    partial[0] = '/';
    partial[1] = '\0';
    vfs_node_t *last_dir = vfs_root();

    const char *cursor = path;
    while (*cursor == '/')
    {
        cursor++;
    }

    while (*cursor)
    {
        const char *start = cursor;
        while (*cursor && *cursor != '/')
        {
            cursor++;
        }
        size_t comp_len = (size_t)(cursor - start);
        if (comp_len == 0)
        {
            while (*cursor == '/')
            {
                cursor++;
            }
            continue;
        }

        if (partial_len > 1)
        {
            partial[partial_len++] = '/';
        }
        if (partial_len + comp_len >= sizeof(partial))
        {
            return NULL;
        }
        memcpy(partial + partial_len, start, comp_len);
        partial_len += comp_len;
        partial[partial_len] = '\0';

        vfs_node_t *dir = vfs_resolve(vfs_root(), partial);
        if (!dir)
        {
            dir = vfs_mkdir(vfs_root(), partial);
        }
        if (!dir)
        {
            return NULL;
        }
        last_dir = dir;

        while (*cursor == '/')
        {
            cursor++;
        }
    }

    return last_dir;
}

