+#include "process.h"
+#include "serial.h"
+
+#define SMP_TRAMPOLINE_PHYS 0x0000000000008000ULL
+#define SMP_BOOT_DATA_PHYS  0x0000000000007000ULL
+#define SMP_BOOT_STACK_SIZE (32 * 1024)
+
+#define MADT_SIGNATURE "APIC"
+#define MADT_LAPIC_ENTRY 0x00
+#define LAPIC_FLAG_ENABLED 0x1
+
+typedef struct __attribute__((packed))
+{
+    char     signature[4];
+    uint32_t length;
+    uint8_t  revision;
+    uint8_t  checksum;
+    char     oem_id[6];
+    char     oem_table_id[8];
+    uint32_t oem_revision;
