{
    if (ticks == 0)
    {
        process_yield();
        return;
    }

    thread_t *thread = g_current_thread;
    if (!thread || thread->is_idle)
    {
        process_yield();
        return;
    }

    uint64_t wake_tick = timer_ticks() + ticks;
