bool lapic_init(void)
{
    uint64_t apic_base = rdmsr(IA32_APIC_BASE_MSR);
    apic_base |= LAPIC_ENABLE_BIT;
    wrmsr(IA32_APIC_BASE_MSR, apic_base);

