{"command":["bash","-lc","python3 - <<'PY'
import subprocess, pathlib, sys, re

cmd = ["bash", "-lc", "rg -l --iglob '*.[chS]' '(serial_write_string|serial_write_char|serial_write_hex64|serial_write_hex8)' src include user"]
result = subprocess.run(cmd, capture_output=True, text=True, check=True)
files = [line.strip() for line in result.stdout.splitlines() if line.strip()]
skip = {
    'src/drivers/serial.c',
    'user/serial_stub.c',
    'include/serial.h'
}

funcs = [
    ("serial_write_hex64", lambda arg: f'serial_printf("%016llX", (unsigned long long)({arg}))'),
    ("serial_write_hex8", lambda arg: f'serial_printf("%02X", (unsigned int)({arg}))'),
    ("serial_write_char", lambda arg: f'serial_printf("%c", {arg})'),
    ("serial_write_string", lambda arg: f'serial_printf("%s", {arg})'),
]


def skip_string(text, idx, quote):
    while idx < len(text):
        c = text[idx]
        if c == '\\':
            idx += 2
            continue
        if c == quote:
            return idx + 1
        idx += 1
    return idx

def skip_line_comment(text, idx):
    while idx < len(text) and text[idx] != '\n':
        idx += 1
    return idx

def skip_block_comment(text, idx):
    while idx < len(text) - 1:
        if text[idx] == '*' and text[idx + 1] == '/':
            return idx + 2
        idx += 1
    return len(text)

def skip_ws_and_comments(text, idx):
    while idx < len(text):
        c = text[idx]
        if c in ' \t\r\n':
            idx += 1
            continue
        if c == '/' and idx + 1 < len(text):
            if text[idx + 1] == '/':
                idx = skip_line_comment(text, idx + 2)
                continue
            if text[idx + 1] == '*':
                idx = skip_block_comment(text, idx + 2)
                continue
        break
    return idx

def parse_call(text, start_idx, name):
    idx = start_idx + len(name)
    while idx < len(text) and text[idx].isspace():
        idx += 1
    if idx >= len(text) or text[idx] != '(':
        return None
    arg_start = idx + 1
    depth = 1
    pos = arg_start
    while pos < len(text) and depth > 0:
        c = text[pos]
        if c == '"':
            pos = skip_string(text, pos + 1, '"')
            continue
        if c == '\'':
            pos = skip_string(text, pos + 1, '\'')
            continue
        if c == '/' and pos + 1 < len(text):
            if text[pos + 1] == '/':
                pos = skip_line_comment(text, pos + 2)
                continue
            if text[pos + 1] == '*':
                pos = skip_block_comment(text, pos + 2)
                continue
        if c == '(':
            depth += 1
        elif c == ')':
            depth -= 1
            if depth == 0:
                arg_end = pos
                pos += 1
                break
        pos += 1
    if depth != 0:
        return None
    tail_start = pos
    tail_end = skip_ws_and_comments(text, tail_start)
    if tail_end >= len(text) or text[tail_end] != ';':
        return None
    semicolon_idx = tail_end
    after_call = semicolon_idx + 1
    arg = text[arg_start:arg_end]
    between = text[tail_start:semicolon_idx]
    return after_call, arg, between

def replace_in_text(text):
    i = 0
    out = []
    changed = False
    length = len(text)
    while i < length:
        c = text[i]
        if c == '"':
            end = skip_string(text, i + 1, '"')
            out.append(text[i:end])
            i = end
            continue
        if c == '\'':
            end = skip_string(text, i + 1, '\'')
            out.append(text[i:end])
            i = end
            continue
        if c == '/' and i + 1 < length:
            nxt = text[i + 1]
            if nxt == '/':
                end = skip_line_comment(text, i + 2)
                out.append(text[i:end])
                i = end
                continue
            if nxt == '*':
                end = skip_block_comment(text, i + 2)
                out.append(text[i:end])
                i = end
                continue
        matched = False
        for name, builder in funcs:
            if text.startswith(name, i):
                prev = text[i - 1] if i > 0 else ''
                if prev.isalnum() or prev == '_':
                    continue
                parsed = parse_call(text, i, name)
                if not parsed:
                    continue
                after_call, arg, between = parsed
                replacement = builder(arg)
                out.append(replacement)
                out.append(between)
                out.append(';')
                i = after_call
                changed = True
                matched = True
                break
        if matched:
            continue
        out.append(c)
        i += 1
    return ''.join(out), changed

for path_str in files:
    if path_str in skip:
        continue
    path = pathlib.Path(path_str)
    text = path.read_text()
    new_text, changed = replace_in_text(text)
    if changed:
        path.write_text(new_text)
