                    serial_format_emit(ctx, 'z');
                }
                if (length_l)
                {
                    serial_format_emit(ctx, 'l');
                    if (length_ll)
                    {
                        serial_format_emit(ctx, 'l');
                    }
                }
                return;
            }
            default:
            {
                serial_format_emit(ctx, '%');
                if (length_z)
                {
                    serial_format_emit(ctx, 'z');
                }
                if (length_l)
                {
                    serial_format_emit(ctx, 'l');
                    if (length_ll)
                    {
