static inline uint64_t read_cr2(void)
{
    uint64_t value;
    __asm__ volatile ("mov %%cr2, %0" : "=r"(value));
    return value;
}

static void halt_forever(void)
{
    for (;;)
    {
        __asm__ volatile ("cli; hlt");
    }
